mat4 skinMatrix () {
  vec4 joints = vec4(a_joints);
  return getJointMatrix(joints.x) * a_weights.x
       + getJointMatrix(joints.y) * a_weights.y
       + getJointMatrix(joints.z) * a_weights.z
